package smallPrograms;

public class PrintNumbersForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for loop
		for (int n = 1; n <= 100; n++) {
			System.out.println(n);
		}

		// array for loop

		int arr[] = { 3, 4, 6, 7 };
		for (int n = 0; n < arr.length; n++) {
			System.out.println(arr[n]);
		}
	}

}
